item.sql:
declare
    l_session_id       number       := #Sid#;
    l_session_serial   number       := #Serial##;
    l_session_instance number       := #Inst_id#;
    l_trace_process    varchar2(1)  := '#4#';
    l_alter_system_sql varchar2(200);  /* alter system statement, used if tracing the process */
    l_session_ospid    number;         /* process number of the target session */
begin
    if sys_context('USERENV', 'INSTANCE') != l_session_instance then
        raise_application_error(-20000, 'This session is not in the local instance');
    end if;

    if l_trace_process is null then
        dbms_monitor.session_trace_enable(
            session_id => l_session_id,
            serial_num => l_session_serial,
            binds      => #1#,
            waits      => #2#,
            plan_stat  => case '#3#'
                              when 'All execs'  then 'ALL_EXECUTIONS'
                              when 'First exec' then 'FIRST_EXECUTION'
                              when 'Never'      then 'NEVER'
                          end
        );
    else
        <<lookup_ospid>>
        begin
            select pro.spid into l_session_ospid
              from v$session ses,
                   v$process pro
             where ses.sid = l_session_id
               and ses.serial# = l_session_serial
               and pro.addr = ses.paddr;
        exception
            when no_data_found then
                raise_application_error(-20000, 'Session not found anymore');
        end lookup_ospid;
        l_alter_system_sql := 'alter system set events ''sql_trace '
                || '{process: ' || to_char(l_session_ospid) || '} '
                || 'plan_stat=' || case '#3#'
                                       when 'All execs'  then 'all_executions'
                                       when 'First exec' then 'first_execution'
                                       when 'Never'      then 'never'
                                   end
                || ',wait=' || lower('#2#') || ',bind=' || lower('#1#') || '''';
        execute immediate l_alter_system_sql;
    end if;
end;

declare
    l_session_id       number       := #Sid#;
    l_session_serial   number       := #Serial##;
    l_session_instance number       := #Inst_id#;
    l_trace_process    varchar2(1)  := '#1#';
    l_alter_system_sql varchar2(200);  /* alter system statement, used if tracing the process */
    l_session_ospid    number;         /* process number of the target session */
begin
    if sys_context('USERENV', 'INSTANCE') != l_session_instance then
        raise_application_error(-20000, 'This session is not in the local instance');
    end if;

    if l_trace_process is null then
        dbms_monitor.session_trace_disable(
            session_id => l_session_id,
            serial_num => l_session_serial
        );
    else
        <<lookup_ospid>>
        begin
            select pro.spid into l_session_ospid
              from v$session ses,
                   v$process pro
             where ses.sid = l_session_id
               and ses.serial# = l_session_serial
               and pro.addr = ses.paddr;
        exception
            when no_data_found then
                raise_application_error(-20000, 'Session not found anymore');
        end lookup_ospid;
        l_alter_system_sql := 'alter system set events ''sql_trace '
                || '{process: ' || to_char(l_session_ospid) || '} off''';
        execute immediate l_alter_system_sql;
    end if;
end;

declare
    l_session_id        number       := #Sid#;
    l_session_serial    number       := #Serial##;
    l_session_instance  number       := #Inst_id#;
    l_session_sqlid     varchar2(13) := '#1#';

begin
    execute immediate 'alter system cancel sql '''
            || to_char(l_session_id) || ',' || to_char(l_session_serial)
            || ',@' || to_char(l_session_instance)
            || case
                   when l_session_sqlid <> 'NULL' then
                       ',' || l_session_sqlid
               end
            || '''';
end;

declare
    l_session_id        number      := #Sid#;
    l_session_serial    number      := #Serial##;
    l_session_instance  number      := #Inst_id#;
    l_immediate_check   varchar2(1) := '#1#';
    l_noreplay_check    varchar2(1) := '#2#';

    e_sess_marked_for_kill exception;
    pragma exception_init(e_sess_marked_for_kill, -31);
begin
    execute immediate 'alter system kill session '''
            || to_char(l_session_id) || ',' || to_char(l_session_serial)
            || ',@' || to_char(l_session_instance) || ''''
            || case when l_immediate_check = 'Y' then ' immediate' end
            || case when l_noreplay_check = 'Y' then ' noreplay' end;
exception
    when e_sess_marked_for_kill then
        null;
end;

declare
    l_session_id        number        := #Sid#;
    l_session_serial    number        := #Serial##;
    l_session_instance  number        := #Inst_id#;
    l_disconnect_mode   varchar2(20)  := '#1#';
    l_method_used       varchar2(40)  := '#2#';

    l_action_reason     varchar2(4000 byte) :=
            substrb(q'{#3#}', 1, 4000);

    e_sess_marked_for_kill exception;
    pragma exception_init(e_sess_marked_for_kill, -31);
begin
    if sys_context('USERENV', 'INSTANCE') != l_session_instance then
        raise_application_error(-20000, 'This session is not in the local instance');
    end if;

    if l_method_used = 'Alter system statement' then
        execute immediate 'alter system disconnect session ''' 
                || to_char(l_session_id) || ',' || to_char(l_session_serial)
                || ''' ' || lower(l_disconnect_mode);
    else
        declare
            l_stmt varchar2(500);
        begin
            l_stmt := q'{begin
                c##pkg_session_helper.disconnect_session(
                    p_session_id       => :session_id,
                    p_session_serial#  => :session_serial,
                    p_reason           => :reason,
                    p_post_transaction => }'
                || case l_disconnect_mode
                       when 'Post_transaction' then
                           'true'
                       else /* IMMEDIATE */
                           'false'
                   end
                || q'{);
            end;}';
            execute immediate l_stmt using l_session_id,
                                           l_session_serial,
                                           l_action_reason;
        end;
    end if;
exception
    when e_sess_marked_for_kill then
        null;
end;
