query.sql:
whenever sqlerror exit failure rollback whenever oserror exit failure rollback
set termout off -- This would trigger the HIGH_LONG_MEM_WARNING message if TERMOUT was ON set long 5000000 set termout on set linesize 10000 set trimspool on set heading off set feedback off set pagesize 0
set termout off set verify off
define spool_directory define spool_filename
column spool_directory noprint new_value spool_directory column spool_filename noprint new_value spool_filename
select NVL(:SPOOL_DIR, :DEFAULT_SPOOL_DIR) as spool_directory from dual;
select NVL(:SPOOL_FILENAME, 'AWR-' || sys_context('USERENV', 'DB_NAME') || case when sys_context('USERENV', 'CDB_NAME') is not null and sys_context('USERENV', 'CON_NAME') <> 'CDB$ROOT' then '-' || sys_context('USERENV', 'CON_NAME') end || case when :INST_ID is not null then '-' || trim('+' from regexp_replace(:INST_ID, '[^[:digit:]]+', '+', 1, 0)) end || '-' || :SNAP_ID_BEGIN || '-' || :SNAP_ID_END || '-' || to_char(sysdate, 'YYYYMMDD_HH24MISS') || '.html') as spool_filename from dual;
cd "&spool_directory"
spool "&spool_filename"
select * from table( dbms_workload_repository.awr_global_report_html( l_dbid => nvl(:DBID, (select dbid from v$database)), l_inst_num => trim(',' from regexp_replace(:INST_ID, '[^[:digit]]+', ',', 1, 0)), l_bid => :SNAP_ID_BEGIN, l_eid => :SNAP_ID_END, l_options => nvl(:OPTIONS, 8) )) ;
spool off
set termout on
select 'AWR report completed: ' || '&spool_filename' || ' (directory: &spool_directory)' from dual;

