item.sql:
            declare
    l_stmt varchar2(32000);
begin
    l_stmt := 'set role '
            || case
                when '#1#' = 'All'          then 'all'
                when '#1#' = 'Specific'     then '#"3"#'
                when '#1#' = 'None'         then 'none'
                when '#1#' = 'Default'      then q'{
    #4#}'
                when '#1#' = 'All Except'   then q'{all except
    #5#}'
               end;
    execute immediate l_stmt;
end;
