query.sql:
whenever sqlerror exit failure rollback whenever oserror exit failure rollback
set long 5000000 set linesize 10000 set trimspool on set heading off set feedback off set pagesize 0
set termout off set verify off
define spool_directory define spool_filename
column spool_directory noprint new_value spool_directory column spool_filename noprint new_value spool_filename
select NVL(:SPOOL_DIR, :DEFAULT_SPOOL_DIR) as spool_directory from dual;
select NVL(:SPOOL_FILENAME, 'stats_oper_report' || '-' || sys_context('USERENV', 'DB_NAME') || '-' || to_char(to_timestamp_tz(:DATETIME_TZ_FROM, 'YYYY-MM-DD HH24:MI:SS TZH:TZM') , 'YYYYMMDD_HH24MISSTZHTZM') || '-' || to_char(case when :DATETIME_TZ_TO is null then systimestamp else to_timestamp_tz(:DATETIME_TZ_TO, 'YYYY-MM-DD HH24:MI:SS TZH:TZM') end , 'YYYYMMDD_HH24MISSTZHTZM') || '-' || nvl(upper(trim(:DETAIL_LEVEL)), 'ALL') || '.html') as spool_filename from dual;
cd "&spool_directory"
spool "&spool_filename"
select * from table(c##pkg_pub_utility.clob_as_varchar2list( dbms_stats.report_stats_operations ( detail_level => nvl(upper(trim(:DETAIL_LEVEL)), 'ALL'), format => 'HTML', since => to_timestamp_tz(:DATETIME_TZ_FROM, 'YYYY-MM-DD HH24:MI:SS TZH:TZM'), until => case when :DATETIME_TZ_TO is null then null else to_timestamp_tz(:DATETIME_TZ_TO, 'YYYY-MM-DD HH24:MI:SS TZH:TZM') end )) ) ;
spool off
set termout on
select 'Report completed: ' || '&spool_filename' || chr(10) || '(directory: &spool_directory)' from dual;
